example.programlisting:
SELECT * FROM table;

-- id  ...
-- 1   ...

INSERT INTO table SET ...;

SELECT * FROM table;

-- id  ...
-- 1   ...
-- 2   ...

INSERT INTO table SET ...;

SELECT * FROM table;

-- id  ...
-- 1   ...
-- 2   ...
-- 3   ...

// reference to trim()
$function_callback = 'trim';

// MyClass::myMethod()
$static_method_callback = 'MyClass::myMethod';
// or
$static_method_callback = array('MyClass', 'myMethod');

// $instance->method()
$c = new MyClass();
$method_callback = array($c, 'method');

// closure:
$closure = function()
{
	return 'foo';
};
